package com.myzyb.appNYB.wxapi;import android.text.TextUtils;import java.util.Map;public class PayResult {	private String resultStatus;	private String result;	private String memo;	public PayResult(Map rawResult) {		if (rawResult == null) {			return;		}		for (Object key :rawResult.keySet()) {			if (TextUtils.equals((CharSequence) key, "resultStatus")) {				resultStatus =(String)(rawResult.get(key));			} else if (TextUtils.equals((CharSequence) key, "result")) {				result = (String)rawResult.get(key);			} else if (TextUtils.equals((CharSequence) key, "memo")) {				memo = (String)rawResult.get(key);			}		}	}	@Override	public String toString() {		return "resultStatus={" + resultStatus + "};memo={" + memo				+ "};result={" + result + "}";	}	/**	 * @return the resultStatus	 */	public String getResultStatus() {		return resultStatus;	}	/**	 * @return the memo	 */	public String getMemo() {		return memo;	}	/**	 * @return the result	 */	public String getResult() {		return result;	}}